mapper.select:
select c.fullname, c.company, c.phone, c.mphone, c.source, c.state, u.name as owner, c.id from tbl_clue c join tbl_user u on c.owner = u.id
c.fullname like '%' #{fullname} '%'
and u.name like '%' #{owner} '%'
and c.phone like '%' #{phone} '%'
and c.mphone like '%' #{mphone} '%'
and c.company like '%' #{company} '%'
and c.source = #{source}
and c.state = #{state}
order by c.createTime desc limit #{skipCount},#{pageSize}
